import React from 'react';
import Login from "./Login";
import RegisterEmail from "./Register/RegisterEmail";
import RegisterProfile from "./Register/RegisterProfile";
import Tab from "./Tab";
import * as Font from 'expo-font';
import { Text, TextInput, TouchableOpacity} from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import { navigationRef } from './RootNavigation';
import * as firebase from 'firebase';

const firebaseConfig = {
  apiKey: "AIzaSyCKaZbEYYPVrvB_IOwbFhFka3RIY3fth2U",
  authDomain: "tipiohapp.firebaseapp.com",
  projectId: "tipiohapp",
  storageBucket: "tipiohapp.appspot.com",
  messagingSenderId: "107612330964",
  appId: "1:107612330964:web:49d73e7b0034bfd87cd441",
  measurementId: "G-NLX5BKB1QM"
};

const Stack = createStackNavigator();

export default class App extends React.Component {

  state = {
    loading: true
  };

  async componentDidMount(){
    firebase.initializeApp(firebaseConfig);
    await Font.loadAsync({
      'DancingScript': require('./assets/fonts/DancingScript-Bold.ttf'),
    });
    this.setState({loading: false});
  }

  render(){
    const { loading } = this.state;
    if(loading){
      return (<Text>Loading</Text>);
    }
    else{
      return (
        <NavigationContainer ref={navigationRef}>
          <Stack.Navigator initialRouteName="Login"
          headerMode="screen"
          gestureEnabled="true"
          screenOptions={{
            headerStyle: {
              backgroundColor: 'grey',
              height : 80,
            },
            headerTintColor: '#fff',
            headerTitleStyle: {
              fontFamily: 'DancingScript',
              fontSize: 30
            },
            headerTitle: "TiPiOh",
          }}>
            <Stack.Screen
              name="Login"
              component={Login}
              options={{
                headerRight: () => (
                  <TouchableOpacity
                    onPress={() => alert('김우석 & 김성원')}
                    style={{marginRight: 10}}>
                    <Text style={{color: 'white'}}>개발자</Text>
                  </TouchableOpacity>
                ),
              }}
            />
            <Stack.Screen
              name="Tab"
              component={Tab}
              options={{
                headerRight: () => (
                  <TouchableOpacity
                    onPress={() => alert('right Menu Clicked')}
                    style={{marginRight: 10}}>
                    <Text style={{color: 'white'}}>right Menu</Text>
                  </TouchableOpacity>
                ),
              }}
            />
            <Stack.Screen
              name="RegisterEmail"
              component={RegisterEmail}
              options={{
                headerRight: () => (
                  <TouchableOpacity
                    onPress={() => navigationRef.current.navigate("RegisterProfile")}
                    style={{marginRight: 10}}>
                    <Text style={{color: 'white'}}>다음</Text>
                  </TouchableOpacity>
                ),
              }}
            />
            <Stack.Screen
              name="RegisterProfile"
              component={RegisterProfile}
              options={{
                headerRight: () => (
                  <TouchableOpacity
                    onPress={() => navigationRef.current.navigate("Login")}
                    style={{marginRight: 10}}>
                    <Text style={{color: 'white'}}>다음</Text>
                  </TouchableOpacity>
                ),
              }}
            />
          </Stack.Navigator>
        </NavigationContainer>
      );
    }
  }
}
